(function () {

	'use strict';

	angular.module('seetodo', ['ionic', 'ionic-material', 'ksSwiper', 'LocalForageModule'])

	.run(function ($ionicPlatform) {
		// Configuration par défault de Ionic
		$ionicPlatform.ready(function () {
			if(window.cordova && window.cordova.plugins && window.cordova.plugins.Keyboard) {
				cordova.plugins.Keyboard.hideKeyboardAccessoryBar(true);
				cordova.plugins.Keyboard.disableScroll(true);
			}
			if(window.StatusBar) {
				StatusBar.styleDefault();
			}
		});
	})

	.config(function ($ionicConfigProvider, $localForageProvider, $stateProvider, $urlRouterProvider) {

		if(ionic.Platform.isAndroid()) { // Vérifie si l'on se trouve sur une plateforme android
			$ionicConfigProvider.scrolling.jsScrolling(false);
		}

		// Configuration de la base de donnée
		$localForageProvider.config({
			driver: 'localStorageWrapper',
			name: 'SeeTodo',
			version: 1.0,
			storeName: 'todos_list',
			description: 'todos list for SeeTodo app'
		});

		// Routage
		$stateProvider
			.state('app', {
				url: '/app',
				abstract: true,
				templateUrl: 'templates/menu.html',
				controller: 'AppCtrl'
			})
			.state('app.main', {
				url: '/main',
				views: {
					'menuContent': {
						templateUrl: 'templates/main.html',
						controller: 'MainCtrl'
					}
				}
			})
			.state('app.active', {
				url: '/main/active',
				views: {
					'menuContent': {
						templateUrl: 'templates/active.html',
						controller: 'MainCtrl'
					}
				}
			})
			.state('app.dones', {
				url: '/main/dones',
				views: {
					'menuContent': {
						templateUrl: 'templates/dones.html',
						controller: 'MainCtrl'
					}
				}
			})
			.state('app.deletes', {
				url: '/main/deletes',
				views: {
					'menuContent': {
						templateUrl: 'templates/deletes.html',
						controller: 'MainCtrl'
					}
				}
			});
		$urlRouterProvider.otherwise('/app/main');
	});
})();

(function () {

	'use strict';

	angular.module('seetodo')

	.controller('AppCtrl', function (ionicMaterialInk, $scope) {
		ionicMaterialInk.displayEffect(); // Actionne les effets de vague sur certains éléments
		if(ionic.Platform.isIOS()) { // Vérifie si l'on se trouve sur une plateforme ios
			$scope.isIos = true;
		}
	})

	.controller('NavCtrl', function ($scope, $ionicSideMenuDelegate) {

		/**
		 * @name ShowMenu
		 * @desc Affiche le slide munu de gauche
		 */
		$scope.showMenu = function () {
			$ionicSideMenuDelegate.toggleLeft();
		};

		/**
		 * @desc Vérifie si le menu est ouvert
		 */
		$scope.$watch(function () {
			return $ionicSideMenuDelegate.isOpen();
		}, function (value) {
			$scope.isOpen = value;
		});

		/**
		 * @desc Vérifie l'url actuelle
		 */
		$scope.$watch(function () {
			return location.hash;
		}, function (value) {
			$scope.url = value;
		});
	})

	.controller('MainCtrl', function (ionicMaterialInk, $ionicPopup, $scope, storage) {

		$scope.newTodo = {};
		$scope.swiper = {};
		$scope.todos = storage.getTodos();

		$scope.next = function () {
			$scope.swiper.slideNext();
		};

		storage.getDatabase()
			.then(function succes(response) {
				console.log('SeeTodo -> Base de donnée chargée correctement');
			}); // On charge la base de donnée

		/**
		 * @name AddTodo
		 * @desc Ajoute une tâche
		 */
		$scope.addTodo = function () {
			var newTodo = $scope.newTodo;
			if(newTodo.title.length > 0) {
				storage.add(newTodo)
					.then(function success(response) {
						console.log('SeeTodo -> Tâche "' + response.id + '" ajoutée en base');
						$scope.swiper.slideNext();
						$scope.next();
					});
			}
		};

		/**
		 * @name DeleteTodo
		 * @desc Supprime une tâche
		 * @param {Object} la tâche à supprimer
		 */
		$scope.deleteTodo = function (todo) {
			if(todo.title.length > 0) {
				storage.delete(todo)
					.then(function success(response) {
						console.log('SeeTodo -> Tâche "' + response.id + '" archivée');
					});
			}
		};

		/**
		 * @name EditTodo
		 * @desc Modifie une tâche
		 * @param {Object} la tâche à modifier
		 */
		$scope.editTodo = function (todo) {
			if(todo.title.length > 0) {
				storage.edit(todo)
					.then(function success(response) {
						console.log('SeeTodo -> Tâche "' + response.id + '" modifiée');
					});
			}
		};

		/**
		 * @name SwithTodo
		 * @desc Inverse l'état d'activation d'une tâche
		 * @param {Object} la tâche à inverser
		 */
		$scope.switchTodo = function (todo) {
			if(todo.title.length > 0) {
				storage.switch(todo)
					.then(function (reponse) {
						console.log('SeeTodo -> Tâche "' + reponse.id + '" inversée');
					});
			}
		};

		/**
		 * @name ShowConfirm
		 * @desc Popup de confirmation avant suppression de la base
		 */
		$scope.showConfirm = function () {
			var confirmPopup = $ionicPopup.confirm({
				cancelText: 'Annuler',
				okType: 'button button-small button-balanced',
				template: 'Êtes-vous sûr de vouloir tout supprimer ? (Supprimera même les tâches actives)',
				title: 'Tout supprimer'
			});

			confirmPopup.then(function (res) {
				if(res) {
					storage.clearAll();
				}
			});
		};

		/**
		 * @name ShowDetailsPopup
		 * @desc Affiche les details d'une tâche
		 * @param {Object} la tâche concernée
		 */
		$scope.showDetailsPopup = function (todo) {
			$scope.todo = todo;
			$ionicPopup.show({
				templateUrl: 'templates/popup_details.html',
				title: 'Details',
				scope: $scope,
				buttons: [{
					text: 'OK'
				}]
			});
		};

		/**
		 * @name ShowEditPopup
		 * @desc Affiche la popup d'édition d'une tâche
		 * @param {Object} la tâche concernée
		 */
		$scope.showEditPopup = function (todo) {
			$scope.todo = todo;
			$ionicPopup.show({
				templateUrl: 'templates/popup_edit.html',
				title: 'Modifier',
				scope: $scope,
				buttons: [{
					text: 'Annuler'
				}, {
					text: 'Sauvegarder',
					type: 'button-balanced',
					onTap: function () {
						$scope.editTodo(todo);
					}
				}]
			});
		};
	});
})();

(function () {

	'use strict';

	angular.module('seetodo')

	.factory('storage', function ($localForage, $q, $timeout) {

		var forage = $localForage;
		var todos = [];

		return {

			/**
			 * @name Add
			 * @desc Ajoute une tâche en base et met à jour le scope
			 * @param {Object} la tâche à ajouter
			 * @returns {Object} une promesse
			 */
			add: function (todo) {
				var thisTodo = {
					activate: true,
					color: false,
					date: new Date(),
					delete: false,
					description: '',
					id: todo.id ? todo.id : Math.floor(Math.random() * 100000) + 1,
					title: todo.title
				};
				var q = $q.defer();
				forage.setItem(thisTodo.id, thisTodo)
					.then(function (result) {
						todos.push(thisTodo);
						q.resolve(result);
					});
				return q.promise;
			},

			/**
			 * @name ClearAll
			 * @desc Supprime la base dans sa totalité et met à jour le scope
			 * @returns {Object} une promesse
			 */
			clearAll: function () {
				var q = $q.defer();
				forage.clear()
					.then(function (result) {
						console.log('SeeTodo -> Toutes les tâches ont bien été supprimées');
						for(var variable in todos) {
							todos[variable] = {};
						}
						q.resolve(result);
					});
				return q.promise;
			},

			/**
			 * @name Delete
			 * @desc Supprime une tâche en base et met à jour le scope
			 * @param {Object} la tâche à supprimer
			 * @returns {Object} une promesse
			 */
			delete: function (todo) {
				var thisTodo = {
					activate: todo.activate,
					color: todo.color,
					date: todo.date,
					delete: true,
					description: todo.description,
					id: todo.id,
					title: todo.title
				};
				var q = $q.defer();
				forage.setItem(thisTodo.id, thisTodo)
					.then(function (result) {
						todos[todos.indexOf(todo)].delete = true;
						q.resolve(result);
					});
				return q.promise;
			},

			/**
			 * @name Edit
			 * @desc Modifie une tâche en base et met à jour le scope
			 * @param {Object} la tâche à modifier
			 * @returns {Object} une promesse
			 */
			edit: function (todo) {
				var q = $q.defer();
				var that = this;
				forage.setItem(todo.id, todo)
					.then(function (result) {
						that.refresh(todo);
						q.resolve(result);
					});
				return q.promise;
			},

			/**
			 * @name Get_Database
			 * @desc Charge la base de données
			 * @returns {Object} une promesse
			 */
			getDatabase: function () {
				if(todos.length === 0) {
					var q = $q.defer();
					forage.iterate(function (value, key) {
							if(angular.isString(value.title)) {
								todos.push(value);
							}
						})
						.then(function (result) {
							q.resolve(result);
						});
					return q.promise;
				}
			},

			/**
			 * @name Get_Todo
			 * @desc Récupère une tâche en base
			 * @param {Object} la tâche à récupérer
			 * @returns {Object} une promesse
			 */
			getTodo: function (todo) {
				var q = $q.defer();
				forage.getItem(todo.id)
					.then(function (result) {
						q.resolve(result);
					});
				return q.promise;
			},

			/**
			 * @name Get_Todos
			 * @desc Renvoi la liste des tâches
			 * @returns {Object} la liste des tâches
			 */
			getTodos: function () {
				return todos;
			},

			/**
			 * @name Refresh
			 * @desc Désactive puis re-active une tâche afin de rénitialiser le swipe
			 * @param {Object} la tâche à actualiser
			 */
			refresh: function (todo) {
				var todoToRefresh = todos[todos.indexOf(todo)];
				todoToRefresh.delete = !todoToRefresh.delete;
				$timeout(function () {
					todoToRefresh.delete = !todoToRefresh.delete;
				}, 0);
			},

			/**
			 * @name Swith
			 * @desc Inverse l'état d'activation d'une tâche en base et met à jour le scope
			 * @param {Object} la tâche à inverser
			 * @returns {Object} une promesse
			 */
			switch: function (todo) {
				var thisTodo = {
					activate: !todo.activate,
					color: todo.color,
					date: todo.date,
					delete: todo.delete,
					description: todo.description,
					id: todo.id,
					title: todo.title
				};
				var q = $q.defer();
				forage.setItem(thisTodo.id, thisTodo)
					.then(function (result) {
						todos[todos.indexOf(todo)].activate = thisTodo.activate;
						q.resolve(result);
					});
				return q.promise;
			}

		};
	});
})();
